var valorIdadeTexto = prompt("Qual a idade (humana) do seu cachorro?")

var valorIdadeNumero = parseFloat(valorIdadeTexto)

var valorIdade = valorIdadeNumero * 7
var valorIdadeFixado = valorIdade

alert(valorIdadeFixado)

